mapper.insert:
INSERT
  INTO
       TB_QNA
       (
		  QNA_ID
		, USER_ID
		, QNA_TITLE
		, QNA_CONTENT
	   )
VALUES
      (
		  PK_QNA_ID.NEXTVAL
		, #{userId}
		, #{qnaTitle}
		, #{qnaContent}
	  )

mapper.update:
UPDATE
        TB_QNA
   SET
        QNA_TITLE = #{qnaTitle}
      , QNA_CONTENT = #{qnaContent}
      , QNA_DATE = SYSDATE
 WHERE
        QNA_ID = #{qnaId}
